	//Устанавливает состояние индикаторов на рабочих местах операторов
	void LED_reload(uint8_t reg, __bit led2ch) __critical
	{
		LEDCLK=0;
		LED2CH=led2ch;
		if(led2ch){
			LED2reg = reg;
		}else{
			LEDreg = reg;
		}
		for(i1 = 0;i1<8;++i1){
			LEDD = reg & 0x80;
			reg<<=1;
			LEDCLK=1;
			LEDCLK=0;
		}
		LEDST=1;
		LEDST=0;
	}
	
